<script setup>
import {onMounted} from 'vue'

const search = ref();
const top = ref();
const totop = ref();
const hasButton = ref(false);

function Totop(){
  top.value.scrollIntoView(true, {
    behavior: 'smooth'
  });
}

function setButton(){
  if(document.documentElement.scrollTop > 350){
    hasButton.value = true;
  }else{
    hasButton.value = false;
  }
}
let isTheme = ref(true);

function changeTheme () {
  if(isTheme.value){
    document.documentElement.setAttribute('data-theme', 'theme1')
    isTheme.value = false;
    return;
  }else{
    document.documentElement.setAttribute('data-theme', 'theme2')
    isTheme.value = true;
  }
}

onMounted(()=>{
  window.addEventListener('scroll',setButton)
})
onUnmounted(()=>{
  window.removeEventListener('scroll',setButton)
})
</script>
